/**
 * Defines ANSI escape sequences for colorizing log levels in the console output.
 *
 * The colors object maps log levels to their respective ANSI color codes.
 * @type {Object.<string, string>}
 */
const colors: { [key: string]: string } = {
	error: "\x1b[31m",
	warn: "\x1b[33m",
	http: "\x1b[32m",
	info: "\x1b[36m",
	verbose: "\x1b[34m",
	debug: "\x1b[37m",
};

/**
 * Returns the provided log level as a colorized string using ANSI escape sequences.
 *
 * The function colorizes the log level based on the provided `level` argument and the colors defined in the `colors` object.
 *
 * @param {string} level - The log level to colorize (e.g., "error", "info", etc.).
 * @returns {string} The colorized log level string.
 */
export const getColorizedLevel = (level: string) => {
	return `[${colors[level] + level.toLocaleUpperCase() + "\x1b[0m"}]`;
};

/**
 * Export the `colors` object for use in other modules.
 * @type {Object.<string, string>}
 */
export default colors;
