/**
 * Defines the hierarchy of log levels for the logging system.
 *
 * The log levels are mapped to numeric values indicating their priority.
 * Lower numbers represent higher priorities. For example, 'error' has the highest priority
 * and 'debug' has the lowest.
 *
 * @type {Object.<string, number>}
 */
const logLevel: { [s: string]: number } = {
	error: 0,
	warn: 1,
	http: 3,
	info: 4,
	verbose: 5,
	debug: 6,
};

/**
 * Maps the loglevel to standard loglevel
 * @param level
 * @returns standard log level
 */
const logLevelMapper = (level: string) => {
	return {
		error: "error",
		warn: "warn",
		info: "info",
		http: "verbose",
		verbose: "verbose",
		debug: "debug",
	}[level];
};

/**
 * Export the `logLevel` object for use in other modules.
 * @type {Object.<string, number>}
 */
export default logLevel;
export { logLevelMapper };
